mapper.select:
select * from (select n.*, rownum as rnum from(
select a.*, (select name from tblTheater where theater_seq = a.theater_seq) as theater_name, (select name from tblMovie where movie_seq = a.movie_seq) as movie_name, (select story from tblMovie where movie_seq = a.movie_seq) as story, (select img from tblMovie where movie_seq = a.movie_seq) as img, nvl((select 'y' from tblTheaterClose where to_char(sysdate, 'yyyy-mm-dd') between start_date and end_date and theater_seq = a.theater_seq), 'n') as close from tblMovieplay a order by movie_play_seq desc
) n) where rnum between #{startIndex} and #{endIndex}
