mapper.select:
		select *
      from (select n.*, rownum as rnum from (select * from vwBookUser where email = #{email} and regdate >= to_char(sysdate, 'yyyy-mm-dd') order by regdate desc) n)
      where rnum between #{startIndex} and #{endIndex}

		select *
      from (select n.*, rownum as rnum from (select * from vwBookUser where email = #{email} and regdate < to_char(sysdate, 'yyyy-mm-dd') order by regdate desc) n)
      where rnum between #{startIndex} and #{endIndex}
