mapper.select:
select * from (
    select n.*, rownum as rnum
    from (
        SELECT c.*, cwc.is_test
        FROM tblCWC cwc
        JOIN tblCourse c ON cwc.course_seq = c.course_seq
    ) n
) where rnum between #{startIndex} and #{endIndex}

mapper.update:
update tblCWC
set is_test = #{isTest}
where course_seq = #{courseSeq}
